DROP DATABASE barack_pergunta;
CREATE DATABASE barack_pergunta;
USE barack_pergunta;

CREATE TABLE usuario_turma_disciplina (
id_usuario_turma_disciplina BIGINT PRIMARY KEY UNIQUE AUTO_INCREMENT NOT NULL,
id_turma_disciplina BIGINT,
id_usuario BIGINT
);

CREATE TABLE turma_disciplina (
id_turma_disciplina BIGINT PRIMARY KEY UNIQUE AUTO_INCREMENT NOT NULL,
id_disciplina BIGINT,
id_turma BIGINT
);

CREATE TABLE comentario (
id_comentario BIGINT PRIMARY KEY UNIQUE AUTO_INCREMENT NOT NULL,
avaliacao TINYINT,
url_imagem VARCHAR(255),
texto_comentario VARCHAR(255) NOT NULL,
anonimo_comentario BOOLEAN,
id_postagem BIGINT,
id_usuario BIGINT
);

CREATE TABLE postagem (
id_postagem BIGINT PRIMARY KEY UNIQUE AUTO_INCREMENT NOT NULL,
favorito BOOLEAN,
url_imagem VARCHAR(255),
texto_postagem VARCHAR(255) NOT NULL,
titulo_postagem VARCHAR(255) NOT NULL,
anonimo_postagem BOOLEAN,
id_usuario BIGINT,
id_turma_disciplina BIGINT,
FOREIGN KEY(id_turma_disciplina) REFERENCES turma_disciplina (id_turma_disciplina)
);
CREATE TABLE prova (
id_prova BIGINT PRIMARY KEY UNIQUE AUTO_INCREMENT NOT NULL,
titulo_prova VARCHAR(100) NOT NULL,
data_prova VARCHAR(12) NOT NULL,
resumo VARCHAR(255),
id_usuario BIGINT,
id_turma_disciplina BIGINT,
FOREIGN KEY(id_turma_disciplina) REFERENCES turma_disciplina (id_turma_disciplina)
);

CREATE TABLE usuario_disciplina (
id_usuario_disciplina BIGINT PRIMARY KEY UNIQUE AUTO_INCREMENT NOT NULL,
id_disciplina BIGINT,
id_usuario BIGINT
);

CREATE TABLE disciplina (
id_disciplina BIGINT PRIMARY KEY UNIQUE AUTO_INCREMENT NOT NULL,
nome_disciplina VARCHAR(50),
ementa TEXT,
bibliografia_basica TEXT,
carga_horaria VARCHAR(5),
conteudo TEXT,
bibliografia_complementar TEXT,
cor VARCHAR(30),
serie TINYINT,
curso VARCHAR(30)
);

CREATE TABLE turma (
id_turma BIGINT PRIMARY KEY UNIQUE AUTO_INCREMENT NOT NULL,
nome_turma VARCHAR(10),
curso_turma VARCHAR(20)
);

CREATE TABLE usuario (
id_usuario BIGINT PRIMARY KEY UNIQUE AUTO_INCREMENT NOT NULL,
nome_usuario VARCHAR(50) NOT NULL,
curso_usuario VARCHAR(12) NOT NULL,
senha VARCHAR(25) NOT NULL,
url_perfil VARCHAR(255),
email VARCHAR(50) NOT NULL UNIQUE,
login VARCHAR(50) NOT NULL
);

CREATE TABLE favorito_postagem (
id_favorito_postagem BIGINT PRIMARY KEY UNIQUE AUTO_INCREMENT NOT NULL,
id_usuario BIGINT,
id_postagem BIGINT,
FOREIGN KEY(id_usuario) REFERENCES usuario (id_usuario),
FOREIGN KEY(id_postagem) REFERENCES postagem (id_postagem)
);

ALTER TABLE usuario_turma_disciplina ADD FOREIGN KEY(id_turma_disciplina) REFERENCES turma_disciplina (id_turma_disciplina);
ALTER TABLE usuario_turma_disciplina ADD FOREIGN KEY(id_usuario) REFERENCES usuario (id_usuario);
ALTER TABLE turma_disciplina ADD FOREIGN KEY(id_disciplina) REFERENCES disciplina (id_disciplina);
ALTER TABLE turma_disciplina ADD FOREIGN KEY(id_turma) REFERENCES turma (id_turma);
ALTER TABLE comentario ADD FOREIGN KEY(id_postagem) REFERENCES postagem (id_postagem);
ALTER TABLE comentario ADD FOREIGN KEY(id_usuario) REFERENCES usuario (id_usuario);
ALTER TABLE postagem ADD FOREIGN KEY(id_usuario) REFERENCES usuario (id_usuario);
ALTER TABLE prova ADD FOREIGN KEY(id_usuario) REFERENCES usuario (id_usuario);
ALTER TABLE usuario_disciplina ADD FOREIGN KEY(id_disciplina) REFERENCES disciplina (id_disciplina);
ALTER TABLE usuario_disciplina ADD FOREIGN KEY(id_usuario) REFERENCES usuario (id_usuario);

